import router from "@/router";
import axios from "../config";
const storeConfig = {
  state: {
    role: null,
  },

  mutations: {
    login(state) {
      state.isLoggedIn = true;

    },
    logout(state) {
      state.isLoggedIn = false;
      state.token = null;

      localStorage.removeItem("isLoggedIn");
      localStorage.removeItem("token");
    },
  },
};

export default storeConfig;
