import { Chart } from 'chart.js/auto';
export default {
  name: 'HotelChart',
  props: {
    chartData: {
      type: Object,
      default: null
    }
  },
  mounted() {
    const ctx = this.$refs.chart.getContext('2d');
    new Chart(ctx, {
      type: 'line',
      data: this.myChart
    });
  },
  computed: {
    myChart() {
      return this.chartData;
    }
  }
};
