const storeConfig = {
  state: {
    role: localStorage.getItem('role'),
  },

  mutations: {
    setRole(state) {
      state.role = localStorage.getItem('role');
      console.log(state.role)
    },
    removeRole(state) {
      state.role = null;
    },
  },
};

const store = createStore(storeConfig)


export default store;
